import React from 'react';
import * as dataMgr from '../dataMgr';
import './timeSpeed.css';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
const axios = require('axios');

class TimeSpeed extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            name:"timeSpeed",
            connectList: new Array(),
            speed: 0
        }
    }

    onclick(){
        var multText = document.getElementById('mult');
        var ipText = document.getElementById('ip');
        var portText = document.getElementById('port');

        // axios.get('http://localhost:999/user', {
        //     params: {
        //         mult: multText.value,
        //         ip: ipText.value,
        //         port:portText.value,
        //     }
        // }).then(function (response) {
        //     console.log("1111111111111111111", response.data);
        // }).catch(function (error) {
        //     console.log("22222222222222222", error);
        // });
    }

    onadd(){
        var connectList = this.state.connectList.slice()
        var ipText = document.getElementById('ip');
        var portText = document.getElementById('port');
        var hasAdd = false
        for (var v in connectList)
        {
            if (v.ip == ipText.value && v.port == portText.value)
                hasAdd = true
        }
        console.log("hasAdd", hasAdd, ipText.value, portText.value)
        if (!hasAdd)
        {
            connectList.push({ip:ipText.value, port:portText.value})
            this.setState({connectList:connectList})
        }
    }
    onspeed()
    {
        var multText = document.getElementById('mult');
        this.setState({speed:multText.value})
    }
    render() {
        var products = this.state.connectList.slice()
        // [{
        //     id: 1,
        //     name: "Item name 1",
        //     price: 100
        // },{
        //     id: 2,
        //     name: "Item name 2",
        //     price: 100
        // },];

        // this.state.connectList.slice()
 
      
        return (
            <div>
                <div className="multdiv">
                    <text className='title'>速度 </text><br/><br/>
                    <text className="multText">加速倍数:</text><input type="text" id="mult" className="multInput"/>
                    <button className="multButton" onClick={this.onclick}> 修改速度 </button><br/>
                </div>
                <text>-------------------------------------------------------------------------------------</text>
                <div className="multdiv">
                    <text className='title'>服务器配置 </text><br/><br/>
                    <text className="ipText">服务器IP:</text><input type="text" id="ip" className="ipInput"/><br/>
                    <text className="portText">端口:</text><input type="text" id="port" className="portInput"/><br/>
                    <button className="addButton" onClick={this.onadd}> 添加 </button><br/>
                </div>
                <text>-------------------------------------------------------------------------------------</text>
                <div className="multdiv">
                    <text className='title'>已添加服务器</text><br/><br/>
                    <BootstrapTable data={products} striped={true} hover={true}>
                        <TableHeaderColumn dataField="ip" isKey={true} dataAlign="center" dataSort={true}>Server IP</TableHeaderColumn>
                        <TableHeaderColumn dataField="port" >Server Port</TableHeaderColumn>
                    </BootstrapTable>
                    <button className="execButton" onClick={this.onclick}> 执行 </button><br/>
                </div>
            </div>
        )
    }
}

dataMgr.regNewTool("时间加速", ()=>{return (<TimeSpeed/>)})
