import React from 'react';
import ReactDOM from 'react-dom';
import "./dataMgr.css"
// ------------------------------------------------------------------------------------------------------
// 功能信息
// ------------------------------------------------------------------------------------------------------
// 功能配置表
var toolConfig = new Array()
// 显示的功能页功能索引
var showToolIndex = 0
// ------------------------------------------------------------------------------------------------------
function createToolObj(name, render){
    return {
        name: name,
        render:render,
    }
}

// 注册新功能
export function regNewTool(name, render){
    if(typeof(render) != "function")
        return false

    if(typeof(name) != "string")
        return false
    
    var to = createToolObj(name, render)
    toolConfig.push(to)
    to.id = toolConfig.length - 1
}
// ------------------------------------------------------------------------------------------------------
// 页面部分
// ------------------------------------------------------------------------------------------------------
// 创建一个功能按钮
function ToolButton(props){
    return(
        <button class="ToolButton" id={props.id} onClick = {props.onClick}>{props.buttonName}</button>
    );
}

// 获取所有功能按钮
function GetShowInfo(selectid){
    var buttons = toolConfig.map(
        (tobj) => {
            return (<ToolButton buttonName={tobj.name} id = {tobj.id} onClick = {() => GetShowInfo(tobj.id)} />)            
        }
    )
    if (selectid != undefined)
        showToolIndex = selectid;

    var showConfig = toolConfig[showToolIndex]
    var toolUI = showConfig.render()
    console.log("toolUI %s", typeof(toolUI))
    return(
        <div className="BaseBorder">
            <div className="ToolButtonBorder">
            {buttons}
            </div>
            <div className="ToolUIBorder">
            {toolUI}
            <div>
                "12131231"
            </div>
            </div>
        </div>
    );
}


export function ShowInfo(selectid){
    return GetShowInfo(selectid)
}
