import React from 'react';
import ReactDOM from 'react-dom';
import {Tabs, Tab} from 'react-bootstrap-tabs';
// ------------------------------------------------------------------------------------------------------
// 功能信息
// ------------------------------------------------------------------------------------------------------
// 功能配置表
var toolConfig = new Array()
// ------------------------------------------------------------------------------------------------------
function createToolObj(name, render, index){
    return {
        name: name,
        render:render,
        index:index,
    }
}

// 注册新功能
export function regNewTool(name, render){
    if(typeof(render) != "function")
        return false

    if(typeof(name) != "string")
        return false
    
    var to = createToolObj(name, render, toolConfig.length)
    toolConfig.push(to)
}
// ------------------------------------------------------------------------------------------------------
// 页面部分
// ------------------------------------------------------------------------------------------------------
// 创建一个功能按钮
class Test1 extends React.Component {
    render() {
        return (
            <button> Test1 </button>
        );
    }
}


class Test2 extends React.Component {
    render() {
        return (
            <button> Test2 </button>
        );
    }
}
regNewTool("Test1", ()=>{return (<Test1/>)})
regNewTool("Test2", ()=>{return (<Test2/>)})


// ts.push(Test1)
// ts.push(Test2) 
// 获取所有功能按钮
function GetShowInfo(f){
    var tabas = toolConfig.map(
        (x) => {
            return (<Tab label={x.name}> {x.render()} </Tab>)
        }
    )
    return(
        <div className="BaseBorder">
            <Tabs className="ToolButtonBorder">
            {tabas}
            </Tabs>
        </div>
    );
}


export function ShowInfo(f){
    return GetShowInfo(f)
}
